mapper.select:
		select count(*) 
		from
		
			ktx_notice 
			
				where ntitle = #{cvalue}
			
			
				where ncontent = #{cvalue}
			
			
				where ntitle = #{cvalue} || ncontent = #{cvalue}
			
		
		
			mycgv_member
			
				where name = #{cvalue}
			
			
				where id = #{cvalue}
			
		 
		
 			ktx_order
			
				where reservnum = #{cvalue}
			
			
				where id = #{cvalue}
