mapper.select:
select rno, id, pass, name, email, pnumber, birth, gender, mdate from (select rownum rno, id, pass, name, email, pnumber, birth, gender , to_char(mdate,'yyyy-mm-dd') mdate from (select id, pass, name, email, pnumber, birth, gender, mdate from ktx_member order by mdate desc)
where name LIKE '%'||#{cvalue}||'%'
where id LIKE '%'||#{cvalue}||'%'
) where rno between #{start} and #{end}
